      name: '',
      email: '',
      message: ''
    }
    }

    handleSubmit(e){
      e.preventDefault();
      axios({
        method: "POST", 
        url:"http://localhost:3002/send", 
        data:  this.state
      }).then((response)=>{
        if (response.data.status === 'success'){
         alert("Thank you for contacting with us!!!")
          this.resetForm()
        }else if(response.data.status === 'fail'){
        console.log("not sent");
        }
